import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import SignInRegister from "./SignUp"

const Dashboard = () => {
  const [user, setUser] = useState(null)
  const navigate = useNavigate();

  /*const fetchPosts = async () => {
    try {
      const response = await fetch('/posts')

      const data = await response.json()
      if (data.error) throw data.message

      setPosts(data)
    } catch (err) {
      console.log(err)
    }
  }

  useEffect(() => {
    fetchPosts()
  }, [])*/

  const register = async (username, email, password) => {
    try {
      const response = await axios.post('http://localhost:5000/register', {username, email, password})
      const data = await response()
      if (data.error) throw data.message

      signIn(username, password)
    } catch (err) {
      console.log(err)
    }
  }

  const signIn = async (username, password) => {
    try {
      const response = await axios.post('http://localhost:5000/signin', {username, password})
      //navigate('/car')
      const data = await response()
      //if (data.error) throw data.message
      console.log(data.username)
      setUser(data)
      navigate('/car');
    } catch (err) {
      console.log(err)
    }
  }

  const signOut = async () => {
    try {
      const response = await fetch('/signout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({}),
      })

      const data = await response.json()
      if (data.error) throw data.message

      setUser(null)
    } catch (err) {
      console.log(err)
    }
  }

  return (
    <div className="App">
      {
        user === null ?
        <SignInRegister
          signIn={signIn}
          register={register}
        />
        : <p>Welcome, {user.username}! <button onClick={e => signOut()}>Sign Out</button></p>
      }


      <h1>Latest Posts</h1>
      <div>
        { user === null ?<p></p> :<p>aboba {user.username}</p> }
        <ul>
          <li>
            <Link to="/car">Home</Link>
          </li>
        </ul>
      </div>
    </div>
  )
}

export default Dashboard;
