import React, { useState, useEffect } from "react";
import { Link, useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import './RouteComputer.css';

const RouteComputer = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const [rc, setRc] = useState(null);
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        const fetchRC = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/route_computer/${id}`, { withCredentials: true, credentials: 'include' });
            setRc(response.data);
        } catch (err) {
            console.error('Ошибка при получении данных');
            navigate('/profile');
        }};
        if (id) {
            fetchRC();
        }
        }, id);

    const handleSave = async () => {
    try {
        await axios.put(`http://localhost:5000/route_computer/${id}`, rc, { withCredentials: true, credentials: 'include' });
        setIsEditing(false);
    } catch (err) {
        toast.error(err.message || 'Ошибка при сохранении');
    }};

    if (!rc) {
        return <div>Загрузка данных...</div>;
    }

    return (
        <div className="rc-container">
            <h2>Маршрутный компьютер</h2>
            <div className="rc-data">
                <div className="data-item">
                    <strong>Модель:</strong>
                    {isEditing ? (
                    <input type="text" value={rc.model} onChange={(e) => setRc({ ...rc, model: e.target.value })}/>
                    ) : rc.model}
                </div>
                <div className="data-item">
                    <strong>Расход топлива:</strong>
                    {isEditing ? (
                    <input type="number" value={rc.fuel_consumption} onChange={(e) => setRc({ ...rc, fuel_consumption: e.target.value })}/>
                    ) : rc.fuel_consumption}
                </div>
                <div className="data-item">
                    <strong>Пробег:</strong>
                    {isEditing ? (
                    <input type="number" value={rc.mileage} onChange={(e) => setRc({ ...rc, mileage: e.target.value })}/>
                    ) : rc.mileage}
                </div>
            </div>
            <div className="buttons">
            {isEditing ? (
            <>
            <button onClick={handleSave}>Сохранить</button>
            <button onClick={() => setIsEditing(false)}>Отмена</button>
            </>
            ) : (
            <button onClick={() => setIsEditing(true)}>Редактировать</button>
            )}
            <Link to="/profile" className="back-button">
            Назад
            </Link>
        </div>
    </div>
);};

export default RouteComputer;
