import React from "react"
import { Link, useNavigate } from "react-router-dom"
import './Profile.css'
import axios from "axios"
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Profile = () => {
    const navigate = useNavigate()
    const user = JSON.parse(localStorage.getItem('user'))

    const signOut = async () => {
      try {
        const response = await axios.post('http://localhost:5000/signout', {}, {withCredentials: true, credentials: 'include'})
        navigate('/')
        const data = await response.json()
        if (data.error) throw data.message
      } catch (err) {
        toast.error(err.message || 'Произошла ошибка при выходе из системы');
      }
    }

    return (
        <div className="profile-container">
          {user ? (
            <div>
              <h2>Профиль пользователя</h2>
              <div className="user-info">
                <p>
                  <strong>Имя пользователя:</strong> {user.username}
                </p>
                <p>
                  <strong>Email:</strong> {user.email}
                </p>
              </div>

              <div className="buttons">
                <Link to="/details" className="button">
                  Автомобиль
                </Link>

                <button onClick={signOut}>Выход</button>

                <Link to="/information" className="button">
                  Информация
                </Link>
              </div>
            </div>
          ) : (
            <p>Пользователь не найден</p>
          )}
        </div>
      );
}

export default Profile
